INSERT INTO response_templates (title, content, category) VALUES
('Стандартный ответ', 'Уважаемый клиент! Спасибо за ваше обращение. Мы рассмотрим его в ближайшее время и свяжемся с вами для уточнения деталей. С уважением, служба поддержки Теннисного центра ЦСКА.', 'other'),
('Качество обслуживания', 'Благодарим за обратную связь. Мы постоянно работаем над улучшением качества обслуживания в нашем центре. Ваше мнение очень важно для нас. Мы проанализируем ситуацию и примем необходимые меры.', 'service_quality'),
('Проблемы с оборудованием', 'Спасибо за информацию о технической проблеме. Наши специалисты уже проверяют оборудование и примут необходимые меры для её устранения. Мы стремимся обеспечить комфортные условия для всех посетителей.', 'equipment_problems'),
('Безопасность', 'Благодарим за сообщение о проблеме безопасности. Служба безопасности центра уже в курсе ситуации и принимает необходимые меры. Безопасность наших посетителей - наш приоритет.', 'safety_concerns'),
('Персонал', 'Спасибо за обратную связь о работе персонала. Мы передадим эту информацию руководителю соответствующего подразделения для принятия мер. Мы ценим каждого сотрудника и стремимся к постоянному улучшению сервиса.', 'staff_behavior'),
('Бронирование', 'Благодарим за обращение по вопросу бронирования. Наша административная служба рассмотрит вашу ситуацию и свяжется с вами для её решения. Мы стремимся сделать процесс бронирования максимально удобным.', 'other'),
('Услуги', 'Спасибо за интерес к услугам нашего центра. Мы передадим ваше пожелание в соответствующий отдел для рассмотрения. Мы постоянно работаем над улучшением и расширением спектра наших услуг.', 'other'); 